import React, { useState, useCallback } from "react";
import { Navbar, Button, Modal, Form } from "react-bootstrap";

export default function Nav() {
  const [show, setShow] = useState(null);
  const [showRegister, setShowRegister] = useState(null);
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");

  const loginShow = (e) => {
    setShow(e);
  };
  const registerShow = (e) => {
    setShowRegister(e);
  };

  const loginFunc = async (e) => {
    e.preventDefault();
    let newLogin
    try {
      const loginData = {
        email,
        password,
      };
      newLogin = await fetch("http://localhost:5000/auth", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(loginData),
      });
      setShow(false);
    } catch (ex) {
      if (ex.response && ex.response.status === 400) {
      const errors = {...newLogin.errors};
      errors.username = ex.response.data;
    }
    }
  };

  const registerFunc = async (e) => {
    e.preventDefault();
    const userData = {
      name,
      email,
      password,
    };
    // eslint-disable-next-line
    try {
      console.log("error");

      const newRegister = await fetch("http://localhost:5000/users", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(userData),
      });
      registerShow(false);
    } catch (ex) {
      if (ex.response && ex.response.status === 400) {
        console.log("error");
        alert("error");
      }
    }
  };

  return (
    <div>
      <Navbar className="navBar">
        <div className="container">
          <Navbar.Brand href="/" className="navText">
            JoinMe<i class="fas fa-users"></i>
          </Navbar.Brand>
          <Form inline>
            <Button onClick={() => loginShow(true)}>Login</Button>
            <Button onClick={() => registerShow(true)}>Register</Button>
          </Form>
        </div>
      </Navbar>

      {/* LOGIN */}
      <Modal show={show}>
        <Modal.Header>Login Modal</Modal.Header>
        <Modal.Body>
          <Form onSubmit={(e) => loginFunc(e)}>
            <Form.Group controlId="email">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="text"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </Form.Group>
            <Form.Group controlId="password">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="text"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Form.Group>
            <Button onClick={() => loginShow(false)}>Cancel</Button>
            <Button type="submit">Login</Button>
          </Form>
        </Modal.Body>
      </Modal>

      {/* REGISTER */}
      <Modal show={showRegister}>
        <Modal.Header>Register Modal</Modal.Header>
        <Modal.Body>
          <Form onSubmit={(e) => registerFunc(e)}>
            <Form.Group controlId="name">
              <Form.Label>User Name</Form.Label>
              <Form.Control
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </Form.Group>
            <Form.Group controlId="email">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="text"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </Form.Group>
            <Form.Group controlId="password">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="text"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Form.Group>
            <Button onClick={() => registerShow(false)}>Cancel</Button>
            <Button type="submit">Register</Button>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
}
