import React, { useState, useCallback, useEffect } from "react";
import { Navbar, Button, Modal, Form } from "react-bootstrap";
import { Data } from "@react-google-maps/api";

export default function Nav() {
  const [show, setShow] = useState(null);
  const [showRegister, setShowRegister] = useState(null);
  const [eventList, setEventList] = useState(null)
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");
  const [events, setEvents] = useState([])

  useEffect(() => {
    async function fetchData() {
      const data = await fetch(`http://localhost:5000/event`);
      const resp = await data.json();
      setEvents(resp);
      console.log("resp", resp);
    }
    fetchData();
  }, []);

  const loginShow = async (e) => {
    setShow(e);
    const data = await fetch("http://localhost:5000/event")
    const events = await data.json();
    setEvents(events);
  };
  const registerShow = (e) => {
    setShowRegister(e);
  };
  const eventShow = (e) => {
    setEventList(e);
  }

  const loginFunc = async (e) => {
    e.preventDefault();
    const loginData = {
      email,
      password,
    };
    const newLogin = await fetch("http://localhost:5000/auth", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      }, 
      body: JSON.stringify(loginData),
    });

    setShow(false);
  };


  const registerFunc = async (e) => {
    e.preventDefault();
    const userData = {
      name,
      email,
      password,
    };
    // eslint-disable-next-line
    const newRegister = await fetch("http://localhost:5000/users", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(userData),
    });
    registerShow(false);
  };

  return (
    <div>
      <Navbar className="navBar">
        <div className="container">
          <Navbar.Brand href="/" className="navText">
            JoinMe<i class="fas fa-users"></i>
          </Navbar.Brand>
          <Form inline>
            <Button onClick={() => loginShow(true)}>Login</Button>
            <Button onClick={() => registerShow(true)}>Register</Button>
            <Button onClick={() => eventShow(true)}>Event List</Button>
          </Form>
        </div>
      </Navbar>

      {/* EVENT LIST */}
      <Modal show={eventList}>
        {events.map(({ id, description }) => (
          <p key={id} value={description}>
            Description:{description}
          </p>
        ))}
      </Modal>


      {/* LOGIN */}
      <Modal show={show}>
        <Modal.Header>Login Modal</Modal.Header>
        <Modal.Body>
          <Form onSubmit={(e) => loginFunc(e)}>
            <Form.Group controlId="email">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="text"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </Form.Group>
            <Form.Group controlId="password">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="text"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Form.Group>
            <Button onClick={() => loginShow(false)}>Cancel</Button>
            <Button type="submit">Login</Button>
          </Form>
        </Modal.Body>
      </Modal>

      {/* REGISTER */}
      <Modal show={showRegister}>
        <Modal.Header>Register Modal</Modal.Header>
        <Modal.Body>
          <Form onSubmit={(e) => registerFunc(e)}>
            <Form.Group controlId="name">
              <Form.Label>User Name</Form.Label>
              <Form.Control
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </Form.Group>
            <Form.Group controlId="email">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="text"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </Form.Group>
            <Form.Group controlId="password">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="text"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Form.Group>
            <Button onClick={() => registerShow(false)}>Cancel</Button>
            <Button type="submit">Register</Button>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
}
