import React, { useState, useEffect } from "react";
import Pagination from "react-bootstrap/Pagination";

export default function Paginations() {
  const [day, setDay] = useState("");
  const [day2, setDay2] = useState("");
  const [day3, setDay3] = useState("");
  const [day4, setDay4] = useState("");
  const [day5, setDay5] = useState("");
  const [day6, setDay6] = useState("");
  const [day7, setDay7] = useState("");

  function myFunction() {
    var d = new Date();
    var weekday = new Array(7);
    weekday[0] = "Sunday";
    weekday[1] = "Monday";
    weekday[2] = "Tuesday";
    weekday[3] = "Wednesday";
    weekday[4] = "Thursday";
    weekday[5] = "Friday";
    weekday[6] = "Saturday";
    weekday[7] = "Sunday";
    weekday[8] = "Monday";
    weekday[9] = "Tuesday";
    weekday[10] = "Wednesday";
    weekday[11] = "Thursday";
    weekday[12] = "Friday";
    weekday[13] = "Saturday";
    // var n = weekday[d.getDay()];
    setDay(weekday[d.getDay()].substring(0, 3).toUpperCase());
    setDay2(weekday[d.getDay() + 1].substring(0, 3).toUpperCase());
    setDay3(weekday[d.getDay() + 2].substring(0, 3).toUpperCase());
    setDay4(weekday[d.getDay() + 3].substring(0, 3).toUpperCase());
    setDay5(weekday[d.getDay() + 4].substring(0, 3).toUpperCase());
    setDay6(weekday[d.getDay() + 5].substring(0, 3).toUpperCase());
    setDay7(weekday[d.getDay() + 6].substring(0, 3).toUpperCase());
  }

  useEffect(() => {
    myFunction();

  }, []);

  const [active, setActive] = useEffect("");

  const clickedPagination = (e) => {
    setActive(e);
  };

  return (
    <div>
      <Pagination size="md">
        <Pagination.Item onClick={() => clickedPagination(1)}>{day}</Pagination.Item>
        <Pagination.Item onClick={() => clickedPagination(2)}>{day2}</Pagination.Item>
        <Pagination.Item onClick={() => clickedPagination(3)}>{day3}</Pagination.Item>
        <Pagination.Item onClick={() => clickedPagination(4)}>{day4}</Pagination.Item>
        <Pagination.Item onClick={() => clickedPagination(5)}>{day5}</Pagination.Item>
        <Pagination.Item onClick={() => clickedPagination(6)}>{day6}</Pagination.Item>
        <Pagination.Item onClick={() => clickedPagination(7)}>{day7}</Pagination.Item>
      </Pagination>
    </div>
  );
}
