import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";

export default function EventForm(props) {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [maxGroupSize, setMaxGroupSize] = useState("");
  const form = props.form

  const createEvent= async (e) => {
      e.preventDefault();
      const eventData = {
          title,
          description,
          startTime,
          endTime,
          maxGroupSize,
      }
      console.log("Event Data:", eventData);
      const newEvent = await fetch("http://localhost:5000/event", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(eventData),
      });
      props.handleClose(); 
  }

  return (
    <div>
      <Form onSubmit={createEvent}>
        <Form.Group controlId="title">
          <Form.Label>Title</Form.Label>
          <Form.Control
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="description">
          <Form.Label>Description</Form.Label>
          <Form.Control
            as="textarea"
            rows="3"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="startTime">
          <Form.Label>Start Time</Form.Label>
          <Form.Control
            type="number"
            value={startTime}
            onChange={(e) => setStartTime(e.target.value)}
          />
          <Form.Text className="text-muted">Enter start time</Form.Text>
        </Form.Group>

        <Form.Group controlId="endTime">
          <Form.Label>End Time</Form.Label>
          <Form.Control
            type="number"
            value={endTime}
            onChange={(e) => setEndTime(e.target.value)}
          />
          <Form.Text className="text-muted">Enter end time</Form.Text>
        </Form.Group>

        <Form.Group controlId="maxGroupSize">
          <Form.Label>Max Group Size</Form.Label>
          <Form.Control
            type="number"
            value={maxGroupSize}
            onChange={(e) => setMaxGroupSize(e.target.value)}
          />
        </Form.Group>

        <Button variant="primary" type="submit">
          JoinMe!
        </Button>
      </Form>
    </div>
  );
}
