import React, { useState, useEffect } from "react";
import Pagination from "react-bootstrap/Pagination";

export default function Paginations() {
  const [day, setDay] = useState("");
  const [day2, setDay2] = useState("");
  const [day3, setDay3] = useState("");
  const [day4, setDay4] = useState("");
  const [day5, setDay5] = useState("");
  const [day6, setDay6] = useState("");
  const [day7, setDay7] = useState("");



  function myFunction() {
    var d = new Date();
    var weekday = new Array(7);
    weekday[0] = "Sunday";
    weekday[1] = "Monday";
    weekday[2] = "Tuesday";
    weekday[3] = "Wednesday";
    weekday[4] = "Thursday";
    weekday[5] = "Friday";
    weekday[6] = "Saturday";
    var n = weekday[d.getDay()];
    setDay(n);
    setDay2(weekday[d.getDay()])
  }

  useEffect(() => {
    myFunction();
  }, []);

  return (
    <div>
      <Pagination size="md">
        <Pagination.Item active>{day}</Pagination.Item>
        <Pagination.Item>TUE</Pagination.Item>
        <Pagination.Item>WED</Pagination.Item>
        <Pagination.Item>THUR</Pagination.Item>
        <Pagination.Item>FRI</Pagination.Item>
        <Pagination.Item>SAT</Pagination.Item>
        <Pagination.Item>SUN</Pagination.Item>
      </Pagination>
    </div>
  );
}
