import React, { useState } from "react";
import { Form } from "react-bootstrap";

export default function EventForm(props) {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [startTime, setStartTime] = useState("")

  return (
    <div>
      <Form onSubmit={createExperience}>
        <Form.Group controlId="title">
          <Form.Label>Title</Form.Label>
          <Form.Control
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="description">
          <Form.Label>Description</Form.Label>
          <Form.Control
            as="textarea"
            rows="3"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="startTime">
          <Form.Label>Start Time</Form.Label>
          <Form.Control
            type="number"
            value={startTime}
            onChange={(e) => setSetStartTime(e.target.value)}
          />
          <Form.Text className="text-muted">Enter start time</Form.Text>
        </Form.Group>

        <Form.Group controlId="endTime">
          <Form.Label>End Time</Form.Label>
          <Form.Control
            type="number"
            value={endTime}
            onChange={(e) => setendStartTime(e.target.value)}
          />
          <Form.Text className="text-muted">Enter end time</Form.Text>
        </Form.Group>

        <Form.Group controlId="maxGroupSize">
          <Form.Label>Max Group Size</Form.Label>
          <Form.Control
            type="number"
            value={maxGroupSize}
            onChange={(e) => setMaxGroupSize(e.target.value)}
          />
        </Form.Group>

        <Button variant="primary" onClick={() => props.handleSubmit()}>
          JoinMe!
        </Button>
      </Form>
    </div>
  );
}
