import React, { useState } from "react";
import { Navbar, Button, Modal, Form } from "react-bootstrap";

export default function Nav(props) {
  const [show, setShow] = useState(null);
  const [showRegister, setShowRegister] = useState(null);
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");
  const [data, setData] = useState(null);

  const loginShow = async (e) => {
    setShow(e);
    const data = await fetch("http://localhost:5000/event");
    const events = await data.json();
  };

  const registerShow = (e) => {
    setShowRegister(e);
  };

  const loginFunc = async (e) => {
    try {
      e.preventDefault();
      const loginData = {
        email,
        password,
      };
      const res = await fetch("http://localhost:5000/auth", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(loginData),
      });
      const data = await res.json();
      localStorage.setItem("token", data.token);
      // todo: setState user object
      props.setUser({ ...data.user, isAuthenticated: true });
    } catch (err) {
      console.log(err);
    }

    setShow(false);
  };

  const registerFunc = async (e) => {
    e.preventDefault();
    const userData = {
      name,
      email,
      password,
    };
    // eslint-disable-next-line
    const newRegister = await fetch("http://localhost:5000/users", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(userData),
    });
    registerShow(false);
  };

  return (
    <div>
      <Navbar className="navBar">
        <div className="container">
          <Navbar.Brand className="navText">
            JoinMe<i class="fas fa-users"></i>
          </Navbar.Brand>

          <Form inline>
            <Button id="navBtn" onClick={() => loginShow(true)}>
              Login
            </Button>
            <Button id="navBtn" onClick={() => registerShow(true)}>
              Register
            </Button>
          </Form>
        </div>
      </Navbar>

      {/* EVENT LIST */}

      {/* LOGIN */}
      <Modal show={show}>
        <Modal.Header>Login</Modal.Header>
        <Modal.Body>
          <Form onSubmit={(e) => loginFunc(e)}>
            <Form.Group controlId="email">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="text"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </Form.Group>
            <Form.Group controlId="password">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="text"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Form.Group>
            <Button onClick={() => loginShow(false)}>Cancel</Button>
            <Button type="submit">Login</Button>
          </Form>
        </Modal.Body>
      </Modal>

      {/* REGISTER */}
      <Modal show={showRegister}>
        <Modal.Header>Register New Account</Modal.Header>
        <Modal.Body>
          <Form onSubmit={(e) => registerFunc(e)}>
            <Form.Group controlId="name">
              <Form.Label>User Name</Form.Label>
              <Form.Control
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </Form.Group>
            <Form.Group controlId="email">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="text"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </Form.Group>
            <Form.Group controlId="password">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="text"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Form.Group>
            <Button onClick={() => registerShow(false)}>Cancel</Button>
            <Button type="submit">Register</Button>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
}
